import React from "react";
import "./Portrait.css";

function Port(props) {
	return 
}
class Portrait extends React.Component{
    constructor(props) {
      super(props);
      this.state = {like: false};
	  this.handleClick = this.handleClick.bind(this);
    }

	handleClick() {
		this.setState(prevState => ({
			like: !prevState.like
		}));
	}

    render(){
        return (
           <div className="Port">
             <div className="Port-head">
				<img className="Port-photo" src={require("../../resource/logo_whova.png")}>
				</img> 
				<p className="Port-name"> Whova
				</p>
             </div>
			 <img src={require("../../resource/image_star.jpg")} width="100%">
			 </img>

			<button className="Port-button" onClick={this.handleClick}>
			{this.state.like ? "Liked": "Disliked"}
			 <img src={this.state.like ? require("../../resource/icon_redheart.png") : require("../../resource/icon_whiteheart.png")} width="20%">
			 </img>
			 </button>
           </div>
       );
    }   
}

export default Portrait;
